import React from 'react';
import { motion } from 'framer-motion';
import { FaCalendar, FaTag } from 'react-icons/fa';
import './Card.css';

const Card = ({ title, excerpt, date, tags, image, link, category, os }) => {
  const handleCardClick = (e) => {
    // Don't navigate if clicking on a tag
    if (e.target.closest('.tag-badge')) {
      return;
    }

    if (category === 'project') {
      window.open(link, '_blank', 'noopener,noreferrer');
    } else {
      window.location.href = link;
    }
  };

  const handleTagClick = (e, tag) => {
    e.stopPropagation(); // Prevent card click
    window.location.href = `/tags/${tag}`;
  };

  return (
    <motion.div
      className="card"
      whileHover={{ 
        y: -8,
        transition: { duration: 0.3 }
      }}
      whileTap={{ scale: 0.98 }}
      onClick={handleCardClick}
      style={{ cursor: 'pointer' }}
    >
      <div className="card-content">
        <div className="card-image-container">
          <img src={image} alt={title} className="card-image" />
          <motion.div 
            className="card-overlay"
            initial={{ opacity: 0 }}
            whileHover={{ opacity: 1 }}
            transition={{ duration: 0.3 }}
          >
            <div className="overlay-content">
              <span className="category-badge">{category}</span>
            </div>
          </motion.div>
        </div>

        <div className="card-body">
          <h3 className="card-title">{title}</h3>

          <p className="card-excerpt">{excerpt}</p>

          <div className="card-meta">
            <div className="card-date">
              <FaCalendar className="meta-icon" />
              <span>{date}</span>
            </div>
          </div>

          {tags && tags.length > 0 && (
            <div className="card-tags">
              <FaTag className="tags-icon" />
              <div className="tags-container">
                {tags.slice(0, 3).map((tag, index) => (
                  <motion.span
                    key={index}
                    className="tag-badge"
                    initial={{ scale: 0, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    transition={{ delay: index * 0.1, duration: 0.3 }}
                    whileHover={{ scale: 1.1 }}
                    onClick={(e) => handleTagClick(e, tag)}
                    style={{ cursor: 'pointer' }}
                  >
                    {tag}
                  </motion.span>
                ))}
                {tags.length > 3 && (
                  <motion.span
                    className="tag-badge more-tags"
                    initial={{ scale: 0, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    transition={{ delay: 3 * 0.1, duration: 0.3 }}
                    whileHover={{ scale: 1.1 }}
                    style={{ cursor: 'pointer' }}
                  >
                    +{tags.length - 3}
                  </motion.span>
                )}
              </div>
            </div>
          )}
        </div>

        <motion.div 
          className="card-hover-effect"
          initial={{ opacity: 0, scale: 0.8 }}
          whileHover={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.3 }}
        />
      </div>
    </motion.div>
  );
};

export default Card;
